// Liste von speziellen WÃ¶rtern, die nicht aufgespalten werden sollen
const specialWords = [
    "paid_search",
    "paid_social",
    "paid_video",
    "organic_search",
    "organic_social",
    "user_reviews"
];

// Join the special words into a regex pattern
const specialWordsPattern = specialWords.join("|");

// Iteriere durch die eingehenden Items
return items.map(item => {
    // Get campaign_id from input data
    const input_data = item.json.body;
    if (input_data && input_data.campaign_id) {
        const campaign_id = input_data.campaign_id;

        // Regex pattern to find all parts, treating special words as single units
        const pattern = new RegExp(`${specialWordsPattern}|[^_]+`, 'g');
        let words = campaign_id.match(pattern);

        // Replace "undefined" words with "organic"
        words = words.map(word => word === 'undefined' ? 'organic' : word);

        // Assign words to respective variables, handling the case when there are fewer words than expected
        const Source = words[0] || "";
        const Medium = words[1] || "";
        const Campaign = words[2] || "";
        const Term = words[3] || "";
        const Content = words[4] || "";

        // Check if Placement is "organic" or "undefined"
        let Placement = "";
        if (words.length > 5 && words[5] !== "organic" && words[5] !== "undefined") {
            Placement = words.slice(5).join("_");
        }

        // Create an object with the UTM parameters
        const output = {
            Source,
            Medium,
            Campaign,
            Term,
            Content,
            Placement,
        };

        // Return the new JSON structure for n8n
        return {
            json: {
                ...item.json,
                output,
            }
        };
    }

    // If no campaign_id is present, return the item unchanged
    return {
        json: item.json
    };
});

