// Liste aller LÃ¤ndervorwahlen
const countryCodes = ["1", "30", "31", "32", "33", "34", "36", "39", "40", "41", "43", "44", "45", "46", "47", "48", "49", "52", "62", "971", "420", "423", "27"];

// Funktion zur Bereinigung der Telefonnummer
function cleanPhoneNumber(phone) {
    // Entferne Leerzeichen, Bindestriche, Klammern und sonstige Sonderzeichen
    phone = phone.replace(/[\s()-]/g, "");

    // Entferne fÃ¼hrende Nullen (vor dem LÃ¤ndercode)
    while (phone.startsWith("0")) {
        phone = phone.substring(1);
    }

    // Wenn keine "+" vorhanden ist, fÃ¼ge sie hinzu (E.164 Format benÃ¶tigt das "+" Zeichen)
    if (!phone.startsWith("+")) {
        phone = "+" + phone;
    }

    // PrÃ¼fe auf doppelte LÃ¤ndervorwahl
    phone = removeDuplicateCountryCode(phone, countryCodes);

    // LÃ¤ndervorwahl validieren und Ã¼berprÃ¼fen, ob nach der LÃ¤ndervorwahl keine Null steht
    for (let code of countryCodes) {
        const pattern = new RegExp(`^(\\+${code})(0+)`); // ÃœberprÃ¼fe auf eine oder mehrere Nullen nach dem LÃ¤ndercode
        if (pattern.test(phone)) {
            // Entferne alle fÃ¼hrenden Nullen nach der LÃ¤ndervorwahl
            phone = phone.replace(pattern, `+$1`); // Ersetze die Nullen durch nichts
        }
    }

    // Entferne alle doppelten '+' Zeichen und lasse nur eines Ã¼brig
    phone = phone.replace(/^\+{2,}/, "+");

    return phone;
}

// Funktion zur ÃœberprÃ¼fung auf doppelte LÃ¤ndervorwahl
function removeDuplicateCountryCode(phone, countryCodes) {
    for (let code of countryCodes) {
        const doubleCodePattern = new RegExp(`^\\+${code}{2}`);
        if (doubleCodePattern.test(phone)) {
            // Entferne die doppelte LÃ¤ndervorwahl
            phone = phone.replace(`+${code}${code}`, `+${code}`);
        }
    }
    return phone;
}

// Telefonnummer bereinigen und formatieren
const formattedPhone = cleanPhoneNumber(phone);

// Berechnung der Anzahl der Zeichen inklusive des Plus-Zeichens
