// Funktion zum Flachmachen des Objekts
function flattenObject(obj, parentKey = '', result = {}) {
  for (const key in obj) {
    const fullKey = parentKey ? `${parentKey}.${key}` : key;

    // Überspringe unerwünschte Keys (nur falls explizit angegeben)
    if (['Zuletzt geändert', 'Datum'].includes(key) || ['Zuletzt geändert', 'Datum'].includes(fullKey)) {
      continue;
    }

    if (typeof obj[key] === 'object' && obj[key] !== null && !Array.isArray(obj[key])) {
      if (obj[key].hasOwnProperty('name')) {
        result[fullKey] = obj[key].name; // Nur Name ins Ergebnis aufnehmen
      } else {
        flattenObject(obj[key], fullKey, result); // Weiter verschachteln
      }
    } else if (typeof obj[key] === 'string' || typeof obj[key] === 'number' || typeof obj[key] === 'boolean') {
      // Zeilenumbrüche und überflüssige Leerzeichen entfernen
      result[fullKey] = typeof obj[key] === 'string' ? obj[key].replace(/\n/g, ' ').trim() : obj[key];

      // Datum umwandeln, wenn der Key "Erstellt" ist
      if (['Erstellt'].includes(key) || ['Erstellt'].includes(fullKey)) {
        const date = new Date(obj[key]);
        if (!isNaN(date)) {
          result[fullKey] = date
            .toLocaleString('de-DE', { day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit' })
            .replace(',', ''); // Datum in dd.mm.yyyy hh:mm Format
        }
      }
    } else {
      result[fullKey] = obj[key];
    }
  }
  return result;
}

// Funktion zur formatierten Ausgabe für ClickUp
function formatForClickUp(data) {
  let formattedOutput = '';
  for (const key in data) {
    // Alle Werte einbeziehen, außer sie sind explizit leer
    if (data[key] !== undefined && data[key] !== null) {
      formattedOutput += `**${key}**:\n${data[key]}\n\n`; // Key in Fettschrift, Wert in neuer Zeile
    }
  }
  return formattedOutput.trim();
}

// Verarbeitung
const inputData = $node['Webhook'].json; // Ersetzen mit tatsächlichem Input in n8n
const flattenedData = flattenObject(inputData.body || inputData); // JSON Body verarbeiten
const formattedData = formatForClickUp(flattenedData);

return {
  normalizedData: flattenedData,
  normalizedData_formatted: formattedData,
};

// Den Markdown-Text aus der vorherigen Node holen
const markdownDescription = $('Get formatted Clickup history data in description').item.json.body.markdown_description;

// Entferne Leerzeilen zwischen Frage und Antwort
const cleanedMarkdown = markdownDescription
    .replace(/:\n\n/g, ':\n') // Entferne zusätzliche Leerzeilen zwischen Frage und Antwort
    .replace(/\n{3,}/g, '\n\n'); // Entferne überflüssige Leerzeilen (mehr als 2)

// Bereinigten Text zurückgeben
return {
    cleanedMarkdown,
};

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
